import { createSlice } from "@reduxjs/toolkit";


//initalize the State

const UserID = "";


//Create a Slice with Export 

export const IdSlice = createSlice({
    name:"Id",//First Property to define the name fo the slice
    UserID,//Second Property is the initial State of your slice 
    //Third property Definig Reducers Which is used to change the state 
    reducers: {
        setId: (state,action)=>{
            state = action.payload;//We are storing the New Id 
        }
    }

})
export const {setId} = IdSlice.actions;////Here we are exporting the action and giveing name related to reducer name

export default IdSlice.reducer;
